  setEditorRouterOutlet(elementID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongmarkdown_go_editor: ["github_com_fullstack_lang_gongmarkdown_go-" + "element-detail", elementID]
      }
    }]);
  }

